import { useState, useEffect } from 'react';
import { productService } from '../services/api';

export const useProducts = (filters = {}, forceMockData = null) => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchProducts = async () => {
    try {
      setLoading(true);
      setError(null);
      
      const data = Object.keys(filters).length > 0 
        ? await productService.getProductsByFilters(filters, forceMockData)
        : await productService.getAllProducts(forceMockData);
      
      setProducts(data);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchProducts();
  }, [JSON.stringify(filters), forceMockData]);

  return { products, loading, error, refetch: fetchProducts };
};
