import axios from 'axios';

const api = axios.create({
  baseURL: process.env.REACT_APP_API_URL || 'http://localhost:3000/api',
  timeout: 15000,
  headers: {
    'Content-Type': 'application/json',
    'Accept': 'application/json'
  }
});

// Add request interceptor for debugging
api.interceptors.request.use(
  (config) => {
    console.log(`ðŸ”„ API Request: ${config.method?.toUpperCase()} ${config.url}`);
    return config;
  },
  (error) => {
    console.error('âŒ API Request Error:', error);
    return Promise.reject(error);
  }
);

// Add response interceptor for debugging and error handling
api.interceptors.response.use(
  (response) => {
    console.log(`âœ… API Response: ${response.status} ${response.config.url}`);
    return response;
  },
  (error) => {
    console.error('âŒ API Response Error:', error.response?.status, error.message);
    return Promise.reject(error);
  }
);

const applyFilters = (products, filters) => {
  return products.filter(product => {
    if (filters.minPrice && product.price < parseFloat(filters.minPrice)) return false;
    if (filters.maxPrice && product.price > parseFloat(filters.maxPrice)) return false;
    if (filters.minPopularity && (product.rating || product.popularity) < parseFloat(filters.minPopularity)) return false;
    return true;
  });
};

export const productService = {
  getAllProducts: async () => {
    try {
      const response = await api.get('/products');
      console.log('ðŸ“¦ Received products from API:', response.data?.length || 0, 'items');
      return response.data || [];
    } catch (error) {
      console.error('âŒ Failed to fetch products from API:', error.message);
      throw new Error(`Failed to fetch products: ${error.message}`);
    }
  },

  getProductsByFilters: async (filters) => {
    try {
      const params = new URLSearchParams();
      
      if (filters.minPrice) params.append('minPrice', filters.minPrice);
      if (filters.maxPrice) params.append('maxPrice', filters.maxPrice);
      if (filters.minPopularity) params.append('minPopularity', filters.minPopularity);
      
      const queryString = params.toString();
      const url = queryString ? `/products?${queryString}` : '/products';
      
      console.log('ðŸ” Fetching filtered products with params:', Object.fromEntries(params));
      
      const response = await api.get(url);
      console.log('ðŸ“¦ Received filtered products from API:', response.data?.length || 0, 'items');
      return response.data || [];
    } catch (error) {
      console.error('âŒ Failed to fetch filtered products from API:', error.message);
      throw new Error(`Failed to fetch filtered products: ${error.message}`);
    }
  },

  // Test API connection
  testConnection: async () => {
    try {
      const response = await api.get('/products');
      return {
        success: true,
        status: response.status,
        data: response.data,
        message: 'API connection successful'
      };
    } catch (error) {
      return {
        success: false,
        status: error.response?.status || 'Network Error',
        error: error.message,
        message: 'API connection failed'
      };
    }
  }
};

export { api };

export default api;
